package org.otherobjects.jetty;import org.mortbay.jetty.Connector;import org.mortbay.jetty.Server;import org.mortbay.jetty.handler.ContextHandler;import org.mortbay.jetty.nio.SelectChannelConnector;import org.mortbay.jetty.webapp.WebAppContext;/** * Simple launcher app to run Jetty inside eclipse. *  * @author rich */public class JettyLauncher{    public static void main(String[] args) throws Exception    {        Server server = new Server();        Connector connector = new SelectChannelConnector();        connector.setPort(8080);        server.setConnectors(new Connector[]{connector});        ContextHandler context = new WebAppContext();        context.setContextPath("/");        context.setResourceBase("./src/main/webapp");        context.setClassLoader(Thread.currentThread().getContextClassLoader());        server.setHandler(context);        server.start();        server.join();    }}