package org.otherobjects.cms;import org.aspectj.lang.ProceedingJoinPoint;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class PerformanceInterceptor{    private final Logger logger = LoggerFactory.getLogger(PerformanceInterceptor.class);    public Object log(ProceedingJoinPoint call) throws Throwable    {        long start = System.currentTimeMillis();        try        {            Object result = call.proceed();            return result;        }        finally        {            long end = System.currentTimeMillis();            long timeMs = end - start;            logger.debug("Method: {} ({}) took: {}ms.", new Object[]{call.toShortString(), call.getArgs()[0], timeMs});        }    }}