package org.otherobjects.cms.controllers;import java.io.IOException;import javax.servlet.ServletException;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import org.apache.commons.logging.Log;import org.apache.commons.logging.LogFactory;import org.springframework.web.servlet.ModelAndView;import org.springframework.web.servlet.mvc.Controller;public class WorkbenchController implements Controller{    protected final Log logger = LogFactory.getLog(getClass());    public ModelAndView handleRequest(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException    {        String path = request.getPathInfo();                path = path.substring(10);                if(path.equals("/") )            path = "otherobjects.resources/templates/workbench.html";        else            path = "otherobjects.resources/templates" + path;                            logger.info("WorkbenchController: " + path);                ModelAndView view = new ModelAndView(path);        return view;    }}